<?phpnamespace Modules\Employee\Admin\Forms;class BonusRequestCancel extends \Classes\Base\Form{    public function defaults()    {        return [];    }    public function filters()    {        return ['comment_cancel' => 'trim'];    }    public function rules()    {        return [            'comment_cancel' => [                'NotEmpty' => 'Введите Комментрий',            ],            'id' => [                'NotEmpty' => 'Нет депремирования',            ],        ];    }    public function save()    {        $mBonus = $this->model('Bonus');        $data = $this->getData(['comment_cancel', 'id']);        $id = $this->getData('id');        $data['request'] = $mBonus::REQUEST_ABORT;        $mBonus->upsert($data);        return  $this->model('Bonus')->getById($id);    }}